    @push('script')
        <script>
            function isMobile() {
                return /iPhone|iPad|iPod|Android|BlackBerry|Opera Mini|IEMobile|WPDesktop/i.test(navigator.userAgent);
            }

            document.addEventListener("DOMContentLoaded", function () {
                var callButton = document.getElementById("call-button");
                if (isMobile()) {
                    callButton.style.display = "inline-flex"; // Show button on mobile
                }
            });
        </script>
        <script>
            // Get the textarea element and the character count element
            const messageTextarea = document.getElementById('message');
            const charCount = document.getElementById('charCount');

            // Update character count on input
            messageTextarea.addEventListener('input', function () {
                const currentLength = messageTextarea.value.length;
                charCount.textContent = `${currentLength} / 500 characters`;

                // Optionally change color when limit is exceeded
                if (currentLength > 500) {
                    charCount.style.color = 'red';
                } else {
                    charCount.style.color = '#6c757d'; // default color
                }
            });
            // -----Country Code Selection
            $("#mobile_code").intlTelInput({
                initialCountry: "PS", // Country code for Palestine
                separateDialCode: true,
            });

            function validatePhoneNumber(input) {
                const value = input.value;
                const errorMessage = document.getElementById('phone_error');

                if (!/^\d{0,10}$/.test(value)) {
                    // Prevent input if it exceeds 10 digits or contains non-numeric characters
                    input.value = value.slice(0, 10).replace(/\D/g, '');
                }

                if (value.length === 10 && /^\d{10}$/.test(value)) {
                    errorMessage.style.display = 'none';
                    input.setCustomValidity('');
                } else {
                    errorMessage.style.display = 'block';
                    input.setCustomValidity('The phone number must be exactly 10 digits.');
                }
            }
        </script>
    @endpush

